package com.syntax.class01;

public class PrimitiveData {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// storing and representing numeric values
		byte box1 = 127;
		
		
		short box2=32767;
		
		int box3=1000000;// popular one
		
		
		long box4=100000009878787878l; // credit cards
	// according to size . at end add lor L in long 
	
	
	
	// storing decimal values
	float variable1=1.99f;// at end f
	
	double variable2=99.99d;// popular
     
	
	//storing single characters
	char container ='A';
			char dollar='$';
			
			
			// storing yes or no 
			
			boolean hot=true;
			boolean tired=false;
			
			
		
			
	System.out.println(container);	// print value of variable	
			;
			
			System.out.println(tired);// case sensitive
			
			
			System.out.println(box1);
			System.out.println(box2);
			System.out.println(box3);
			System.out.println(box4);
			
			System.out.println(variable1);
			System.out.println(variable2);
			
			System.out.println(container);
			System.out.println(dollar);
			
			System.out.println(hot);
			System.out.println(tired);
			
			
	}

}
